mapper.select:
SELECT IFNULL(MAX(t.sort), 0)
FROM base_config t
WHERE t.del_state = 0
  AND t.buzz_modal = #{buzzModal}
  AND t.type = #{type}
  AND t.belong_user_id = #{belongUserId}

UPDATE base_config
SET default_scene = '0'
WHERE buzz_modal =  #{buzzModal}
  AND type =  #{type}
  AND belong_user_id = #{belongUserId}
